import React, { useState } from 'react';

const App = () =>{

    let newTime = new Date().toLocaleTimeString(); 
    const state = useState()
    const [time, settime] = useState(newTime)

    const updateTime = () => {
        newTime;
        settime(newTime)
    }

    return(
        <>
            <h1>{time}</h1>
            <button onClick={updateTime}>Get Time</button>
        </>
    )
}

export default App;
